pragma solidity >= 0.5.0;

contract CoShoe{

    struct Shoe{
        address owner;
        string name;
        string image;
        bool sold;
    }

    address public minter;
    mapping(address => uint) public tokens;

    uint public price = 0.5 ether;  // change it to gwei;
    uint public shoesSold = 0;
    Shoe[] public shoe; //relate struct with tokens

    constructor() public{
        minter = msg.sender;
        require(msg.sender == minter, "Function can only be called by minter.");
        //require(amount == 100, "100 CoTokens");
        tokens[minter] = 100;
    }

    function noOfTokens() public view returns (uint){
        return tokens[minter];
    }

    function buyShoe(string memory _name, string memory _image) public payable{
        //This function can only mint 100 tokens
        require(shoesSold < 100, "Sold Out of Shoes");
        require(msg.value == price, "Price of a shoe is 0.5 ether");
        //A buyer choses the name and image of the shoe by putting the below inputs
        shoe.push(Shoe({
            owner: msg.sender,
            name: _name,
            image: _image,
            sold: true
        }));
        //The minter issues a token to the buyer
        tokens[minter] -= 1;
        //The buyer(address) gains the shoe token
        tokens[msg.sender] += 1;
        //A pair of shoe is sold
        shoesSold++;
    }
    function checkPurchases(address caller) public returns(bool[] memory) {
        require(caller == msg.sender,"checkPurchases for caller function");
        bool[] memory trueBool = new bool[](shoesSold);

       for (uint i=0;i<shoesSold;i++) {
           Shoe storage shoes = shoe[i];
           if (shoes.owner == caller){
               shoes.sold = true;
               trueBool[i] = true;
           }
           else{
               shoes.sold = false;
               trueBool[i] = false;
           }
       }
       return trueBool;
    }
}
